import React from "react";
import {setVisibilityFilter} from "../actions"
import {useDispatch} from 'react-redux'



function Filter (){
    const dispatch = useDispatch();
    const getStyle = () =>{
        return color = "#333"
    }
    
    return (
        <div className="filterButton">
            <button className="SHOW_ALL" onClick={()=>dispatch(setVisibilityFilter('SHOW_ALL'))} style={getStyle}>All</button>
            <button className="SHOW_ACTIVE" onClick={()=>dispatch(setVisibilityFilter('SHOW_ACTIVE'))}>Active</button>
            <button className="SHOW_COMPLETED" onClick={()=>dispatch(setVisibilityFilter('SHOW_COMPLETED'))}>Completed</button>
        </div>
    )
}

export default Filter;
