import cookie from 'react-cookies'

const initialState = cookie.get("theme") || 'light-theme';

const themeChanger = (state = 'light-theme', action) => {
  switch (action.type) {
    case "SET_THEME":
      return action.theme;
    default:
      return state;
  }
};

export default themeChanger;
