import React from "react";
import {Themes, setTheme} from "../actions"
import {useSelector, useDispatch} from 'react-redux'



function ThemeChanger (){
    const dispatch = useDispatch();
    
    return (
        <div>
            <span>Themes: </span>
            {/* <button onClick={dispatch(setTheme('light-theme'))}>Light</button> */}
            <button onClick={dispatch(setTheme('dark-theme'))}>Dark</button>
            {/* <button onClick={dispatch(setTheme('green-theme'))}>Green</button> */}
        </div>
    )
}

export default ThemeChanger;
