import React from 'react'
// import DeleteTodo from '../containers/DeleteTodo'
import PropTypes from 'prop-types'
import {deleteTodo} from "../actions"
import {useDispatch} from 'react-redux'


function Todo ({id, onClick, completed, text }){
  const dispatch = useDispatch();
  
  return(
    <div
      onClick={onClick}
      style={{ textDecoration: completed ? 'line-through' : 'none'}}
    >
      <input id="todo-checkbox" type="checkbox" checked={completed} readOnly></input>
      {text}
      <button className="delete-button" onClick={()=>dispatch(deleteTodo(key))}>Delete</button>
    </div>
  )
}

Todo.propTypes = {
  onClick: PropTypes.func.isRequired,
  completed: PropTypes.bool.isRequired,
  text: PropTypes.string.isRequired
}

export default Todo
